import { Box, Button, Grid, InputBase, TextField } from '@material-ui/core';
import { withStyles } from '@material-ui/styles';
import React from 'react';

const style = (theme) => ({
  root: {},
  searchInput: {
    background: '#fff',
    width: '100%',
    height: 40,
    fontSize: 13,
    color: 'rgb(0, 0, 0)',
    '& .MuiInputBase-input': {
      padding: '0px 12px'
    }
  }
});

const Header = (props) => {
  const { classes } = props;
  return (
    <Grid
      container
      style={{ background: 'rgb(1,127,255)', padding: '20px 0px' }}
      className={classes.root}
    >
      <Grid
        item
        xs={2}
        style={{
          display: 'flex',
          width: 300,
          justifyContent: 'center',
          alignItems: 'center'
        }}
      >
        <a href='/' style={{ width: 60, height: 40 }}>
          <img
            style={{ width: '100%', height: '100%' }}
            src='https://salt.tikicdn.com/ts/upload/ae/f5/15/2228f38cf84d1b8451bb49e2c4537081.png'
          />
        </a>
      </Grid>
      <Grid item container xs={7} style={{ display: 'flex' }}>
        <Grid item xs={10}>
          <InputBase
            className={classes.searchInput}
            placeholder='Tìm sản phẩm, danh mục hay thương hiệu mong muốn'
          />
        </Grid>
        <Grid item xs={2}>
          <Button
            style={{
              background: 'rgb(13,92,182)',
              color: '#fff',
              textTransform: 'none'
            }}
          >
            <img
              style={{ width: 20, height: 20 }}
              src='https://salt.tikicdn.com/ts/upload/ed/5e/b8/8538366274240326978318348ea8af7c.png'
            />
            <label>Tìm kiếm</label>
          </Button>
        </Grid>
      </Grid>
      <Grid item xs={3}>
        <img
          style={{ width: 32, height: 32 }}
          src='https://salt.tikicdn.com/ts/upload/67/de/1e/90e54b0a7a59948dd910ba50954c702e.png'
        />
        <label>Đăng nhập/Đăng kí</label>
        <label>Tài khoản</label>
        <img
          style={{ width: 32, height: 32 }}
          src='https://salt.tikicdn.com/ts/upload/40/44/6c/b80ad73e5e84aeb71c08e5d8d438eaa1.png'
        />
        <label>Giỏ hàng</label>
      </Grid>
    </Grid>
  );
};

export default withStyles(style)(Header);
